CREATE TABLE Students (
student_id BIGINT(10) PRIMARY KEY,
name VARCHAR(100),
last_name VARCHAR(100)
);

CREATE TABLE Groupe (
group_id BIGINT(10) PRIMARY KEY,
name VARCHAR(100),
description VARCHAR,
class_id BIGINT
);

CREATE TABLE StudentInGroup (
id_user BIGINT(10),
id_group BIGINT(10)
);

CREATE TABLE Teachers (
teacher_id BIGINT(10) PRIMARY KEY,
name VARCHAR (100),
last_name VARCHAR(100),
group_id BIGINT(10)
);

CREATE TABLE Class (
class_id BIGINT PRIMARY KEY,
number VARCHAR(5)
);

CREATE TABLE Courses (
course_id BIGINT(10) PRIMARY KEY,
name VARCHAR(100),
description VARCHAR(100)
);

CREATE TABLE TeacherToCourse (
teacher_id BIGINT,
course_id BIGINT
);

CREATE TABLE StudentToCourse (
student_id BIGINT,
course_id BIGINT
);

