CREATE TABLE materias {
    id SERIAL PRIMARY KEY,
    nome VARCHAR(255) NOT NULL,
    descricao TEXT,
    carga_horaria INT NOT NULL
};
INSERT INTO materias (nome, descricao, carga_horaria) VALUES
