#include <stdio.h>
#include <string.h>

struct person{
    char name[5];
    int age;
    float high;
};

void data(struct person *p){
    printf("Name: %s\n", p->name);
    printf("Age: %d\n", p->age);
    printf("High: %f\n", p->high);
}

int main(void){
    
    struct person p1;
    
    strcpy(p1.name, "Juan");
    p1.age = 24;
    p1.high = 1.6;
    
    data(&p1);
    
    return 0;
}