#include <iostream>

using namespace std;

struct person{
    /* data */
    string name;
    int age;
};

void puntero(person *p, int age){p->age = age;}

int main (){
    person p1 = {"Juan", 24};

    cout << "Name: " << p1.name << ", Age: " << p1.age << endl;

    puntero(&p1, 25);

    cout << "Name: " << p1.name << ", Age: " << p1.age << endl;

    return 0;
}